mapper.insert:
insert into member_detail
  (
    name
    , birthday
    , mobile_no
    , email
    , zipcode
    , address
    , address_detail
  )
values
  (
    #{name}
    , #{birthday}
    , #{mobileNo}
    , #{email}
    , #{zipcode}
    , #{address}
    , #{addressDetail}
  )

insert into member
  (
    login_id
    , password
    , member_detail_id
  )
values
  (
    #{loginId}
    , #{password}
    , #{memberDetailId}
  )

mapper.update:
update member_detail
set
  name=#{name}
  , birthday=#{birthday}
  , mobile_no=#{mobileNo}
  , email=#{email}
  , zipcode=#{zipcode}
  , address=#{address}
  , address_detail=#{addressDetail}
where 1=1
  and id=(select member_detail_id from member where login_id=#{loginId} and leaved=false)

mapper.select:
select
  a.login_id
from member a
inner join member_detail b on a.member_detail_id = b.id
where 1=1
  and b.name=#{name}
  and b.mobile_no=#{mobileNo}
  and b.email=#{email}
  and a.leaved=false

select
  a.login_id
from member a
inner join member_detail b on a.member_detail_id = b.id
where 1=1
  and b.name=#{name}
  and b.mobile_no=#{mobileNo}
  and b.email=#{email}
  and a.login_id=#{loginId}
  and a.leaved=false

mapper.update:
update member
set
  password=#{password}
where 1=1
  and login_id=#{loginId}
